import React, { useEffect, useState } from "react";
import { Box, Tab, Tabs, Typography } from "@mui/material";
import pages from "../../../pagesConfig";

import { useParams, useNavigate } from "react-router-dom";
import { LayoutGrid } from "./LayoutGrid/LayoutGrid";
import { IconFileDatabase } from "@tabler/icons";
import { MessageBox } from "components/MessageBox";

const PageLayoute = () => {
  const [currentPage, setCurrentPage] = useState("");
  const [currentPageConfig, setCurrentPageConfig] = useState(null);
  const [charts, setCharts] = useState([]);
  const [value, setValue] = useState(0);
  const navigate = useNavigate();

  const { page, sub, group } = useParams();
  console.log(currentPageConfig);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    if (group) {
      setCurrentPage("group");
      return;
    }
    if (sub) {
      setCurrentPage("sub");
      return;
    }
    if (page) {
      setCurrentPage("page");
      return;
    }
  }, [page, sub, group]);

  useEffect(() => {
    if (currentPage) {
      let data = {};
      if (page) {
        data = pages.find((elem) => elem.id === page);
        if (!data) navigate("/404");
      }
      if (sub) {
        data = data.children.find((elem) => elem.id === sub);
        if (!data) navigate("/404");
      }

      setCurrentPageConfig(data);
    }
  }, [currentPage, group, navigate, page, sub]);

  useEffect(() => {
    if (currentPage) {
      let data = {};
      if (page) {
        data = pages.find((elem) => elem.id === page);
      }
      if (sub) {
        data = data.children.find((elem) => elem.id === sub);
      }
      if (group) {
        data = data.children.find((elem) => elem.id === group);
      }
      console.log(data);
      data.charts?.length > 0 ? setCharts(data.charts) : setCharts([]);
    }
  }, [currentPage, group, page, sub]);

  useEffect(() => {
    if (currentPageConfig && currentPageConfig.children) {
      if (currentPage === "sub" && currentPageConfig.children.length > 0) {
        navigate(currentPageConfig.children[0].url);
      }
      if (currentPage === "group" && currentPageConfig.children.length > 0) {
        const index = currentPageConfig.children.findIndex(
          (elem) => elem.id === group
        );
        setValue(index);
      }
    }
  }, [currentPage, currentPageConfig, group, navigate]);

  return (
    <Box sx={{ width: "100%", position: "relative" }}>
      {currentPageConfig && (
        <>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              backgroundColor: "#fff",
              borderRadius: "0 0 12px 12px",
              position: "relative",
              boxShadow: "0 0 10px 0 rgba(0,0,0,0.3)",
              mr: { lg: "32px" },
              ml: { lg: "32px" },
              top: { lg: "-20px" },
              left: "0",
              pt: "8px",
              pb: "8px",
            }}
          >
            {currentPageConfig.children && (
              <Box sx={{ maxWidth: "100%" }}>
                <Tabs
                  value={value}
                  onChange={handleChange}
                  variant="scrollable"
                  scrollButtons="auto"
                  aria-label="scrollable auto tabs example"
                >
                  {currentPageConfig.children.map((item) => (
                    <Tab
                      key={item.id}
                      label={item.title}
                      onClick={() => navigate(item.url)}
                    />
                  ))}
                </Tabs>
              </Box>
            )}
          </Box>
          {charts.length === 0 ? (
            <Box sx={{ mt: "20vh" }}>
              <MessageBox text={"На цій сторінці графіки відсутні"} />
            </Box>
          ) : (
            <LayoutGrid charts={charts} />
          )}
          <Box
            sx={{
              height: "100%",
              width: "1px",
              boxShadow: "0 0 10px 0 rgba(0,0,0,0.3)",
              backgroundColor: "#eef2f6",
              position: "absolute",
              right: "-20px",
              top: "-20px",
            }}
          />
        </>
      )}
    </Box>
  );
};

export default PageLayoute;
