import * as React from "react";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import { Select as MuiSelect } from "@mui/material";
import * as SC from "./FilterSelects.styled";

export const Select = ({ selectConfig }) => {
  const [value, setValue] = React.useState("");
  const { id, title, options } = selectConfig;

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <SC.FilterSelectsItem sx={{ minWidth: 120 }}>
      <FormControl sx={{ color: "white", borderColor: "white" }}>
        <InputLabel sx={{ color: "white" }} id={`${id}-label`}>
          {title}
        </InputLabel>
        <MuiSelect
          labelId={`${id}-label`}
          id={id}
          value={value}
          label={title}
          autoWidth
          sx={{ minWidth: 120 }}
          onChange={handleChange}
        >
          {options.map((value, index) => (
            <MenuItem key={index} value={value}>
              {value}
            </MenuItem>
          ))}
        </MuiSelect>
      </FormControl>
    </SC.FilterSelectsItem>
  );
};
