import Chart from "react-apexcharts";
import React, { useState, useEffect } from "react";

export const ChartComponent = ({ chartData }) => {
  const [chartSetings, setChartSetings] = useState(chartData);

  useEffect(() => {
    setChartSetings(chartData);
  }, [chartData]);

  return (
    <div style={{ width: "800px" }}>
      <Chart
        type={chartSetings.type}
        options={chartSetings.options}
        series={chartSetings.series}
        width="100%"
      />
    </div>
  );
};
