import React, { useEffect, useState } from "react";
import { Box } from "@mui/material";
import pages from "../../../pagesConfig";

import { useParams } from "react-router-dom";
import { LayoutGrid } from "./LayoutGrid/LayoutGrid";

const PageLayoute = () => {
  const [currentPage, setCurrentPage] = useState();
  const [currentPageConfig, setCurrentPageConfig] = useState(null);
  const { page, sub, group } = useParams();

  useEffect(() => {
    if (group) {
      setCurrentPage(group);
      return;
    }
    if (sub) {
      setCurrentPage(sub);
      return;
    }
    if (page) {
      setCurrentPage(page);
      return;
    }
  }, [page, sub, group]);

  useEffect(() => {
    if (currentPage) {
      let data = {};
      if (page) {
        data = pages.find((elem) => elem.id === page);
      }
      if (sub) {
        data = data.children.find((elem) => elem.id === sub);
      }
      if (group) {
        data = data.children.find((elem) => elem.id === group);
      }
      setCurrentPageConfig(data);
    }
  }, [currentPage, group, page, sub]);

  console.log(currentPageConfig);

  return (
    <Box sx={{ width: "100%", position: "relative" }}>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          backgroundColor: "#fff",
          borderRadius: "12px",
          pt: "12px",
          pb: "8px",
        }}
      >
        <h1>{currentPageConfig.title}</h1>
      </Box>
      <LayoutGrid
        currentPage={currentPage}
        currentPageConfig={currentPageConfig}
      />
      <Box
        sx={{
          height: "100%",
          width: "1px",
          boxShadow: "0 0 10px 0 rgba(0,0,0,0.3)",
          backgroundColor: "#eef2f6",
          position: "absolute",
          right: "-20px",
          top: "-20px",
        }}
      />
    </Box>
  );
};

export default PageLayoute;
