import React, { useState, useEffect } from "react";
import Chart from "react-apexcharts";

export const ChartGenerator = () => {
  const [chartData, setChartData] = useState({
    options: {},
    series: [{ name: "series-1", data: [30, 40, 45, 50, 49, 60, 70] }],
  });
  const [chartType, setChartType] = useState("bar");
  console.log(chartData);

  const handleChartTypeChange = (e) => {
    setChartData((prevState) => {
      console.log(e.target.value);
      return { ...prevState, series: e.target.value };
    });
  };

  return (
    <div>
      <form>
        <select
          value={chartData.series}
          multiple
          onChange={handleChartTypeChange}
        >
          <option value={[]}>Bar</option>
          <option
            value={[{ name: "series-2", data: [20, 30, 55, 20, 39, 10, 50] }]}
          >
            Line
          </option>
          <option value={[]}>Area</option>
          <option value={{}}>Pie</option>
          <option value={[]}>Donut</option>
        </select>
        <button type="submit">Submit</button>
      </form>
      <Chart
        type={"bar"}
        options={chartData.options}
        series={chartData.series}
        width="500"
      />
    </div>
  );
};
