import React, { useEffect, useState } from "react";
import { Box, Tab, Tabs } from "@mui/material";
import pages from "../../../pagesConfig";

import { useParams, useNavigate } from "react-router-dom";
import { LayoutGrid } from "./LayoutGrid/LayoutGrid";

const PageLayoute = () => {
  const [currentPage, setCurrentPage] = useState();
  const [currentPageConfig, setCurrentPageConfig] = useState(null);
  const [value, setValue] = React.useState(0);
  const navigate = useNavigate();

  const { page, sub, group } = useParams();

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    if (group) {
      setCurrentPage(group);
      return;
    }
    if (sub) {
      setCurrentPage(sub);
      return;
    }
    if (page) {
      setCurrentPage(page);
      return;
    }
  }, [page, sub, group]);

  useEffect(() => {
    if (currentPage) {
      let data = {};
      if (page) {
        data = pages.find((elem) => elem.id === page);
      }
      if (sub) {
        data = data.children.find((elem) => elem.id === sub);
      }
      if (group) {
        data = data.children.find((elem) => elem.id === group);
      }
      setCurrentPageConfig(data);
    }
  }, [currentPage, group, page, sub]);

  console.log(currentPageConfig);

  return (
    <Box sx={{ width: "100%", position: "relative" }}>
      {currentPageConfig && (
        <>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              backgroundColor: "#fff",
              borderRadius: "0 0 12px 12px",
              position: "relative",
              boxShadow: "0 0 10px 0 rgba(0,0,0,0.3)",
              mr: "32px",
              ml: "32px",
              top: "-20px",
              left: "0",
              pt: "8px",
              pb: "8px",
            }}
          >
            <h2>{currentPageConfig.title}</h2>
            {currentPageConfig.children && (
              <Box sx={{ maxWidth: "100%" }}>
                <Tabs
                  value={value}
                  onChange={handleChange}
                  variant="scrollable"
                  scrollButtons="auto"
                  aria-label="scrollable auto tabs example"
                >
                  {currentPageConfig.children.map((item) => (
                    <Tab label={item.title} />
                  ))}
                </Tabs>
              </Box>
            )}
          </Box>
          <LayoutGrid
            currentPage={currentPage}
            currentPageConfig={currentPageConfig}
          />
          <Box
            sx={{
              height: "100%",
              width: "1px",
              boxShadow: "0 0 10px 0 rgba(0,0,0,0.3)",
              backgroundColor: "#eef2f6",
              position: "absolute",
              right: "-20px",
              top: "-20px",
            }}
          />
        </>
      )}
    </Box>
  );
};

export default PageLayoute;
