import React, { useState, useEffect } from "react";
import Chart from "react-apexcharts";

const test = {
  1: [{ name: "series-2", data: [20, 30, 55, 20, 39, 10, 50] }],
  2: [{ name: "series-3", data: [10, 20, 35, 10, 29, 20, 40] }],
  3: [{ name: "series-4", data: [40, 50, 65, 40, 59, 50, 60] }],
};

export const ChartGenerator = () => {
  const [chartData, setChartData] = useState({
    options: {},
    series: [{ name: "series-1", data: [30, 40, 45, 50, 49, 60, 70] }],
  });
  const [chartType, setChartType] = useState("bar");
  console.log(chartData);

  const handleChartDataSeriesChange = (e) => {
    setChartType(e.target.value);
  };

  return (
    <div>
      <form>
        <select value={chartType} onChange={handleChartDataSeriesChange}>
          <option value="1">Bar</option>
          <option value="2">Line</option>
          <option value="3">Area</option>
          <option value="4">Pie</option>
          <option value="5">Donut</option>
        </select>
        <button type="submit">Submit</button>
      </form>
      <Chart
        type={"bar"}
        options={chartData.options}
        series={test[chartType]}
        width="500"
      />
    </div>
  );
};
