import { useState } from "react";
import { Responsive, WidthProvider } from "react-grid-layout";
const ResponsiveGridLayout = WidthProvider(Responsive);

const breakpoints = { lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0 };
const cols = { lg: 12, md: 10, sm: 6, xs: 4, xxs: 2 };

const ResponsiveLayout = ({ layoutData, children }) => {
  const [
    layout,
    // setLayout
  ] = useState(JSON.parse(localStorage.getItem("layout")) || layoutData);

  // для тесту

  // збереження зміни координат
  const onLayoutChange = (newLayout) => {
    setLayout(newLayout);
    localStorage.setItem("layout", JSON.stringify(newLayout));
  };

  return (
    <ResponsiveGridLayout
      className="layout"
      layouts={{ lg: layout }}
      breakpoints={breakpoints}
      cols={cols}
      rowHeight={100}
      // useCSSTransforms - оптимізація
      useCSSTransforms={true}
      // compactType - прилипання - горизонтальне або вертикальне
      compactType={"horizontal"}
      // autoSize={true}
      // onLayoutChange={onLayoutChange}
      preventCollision={true}
    >
      {children}
    </ResponsiveGridLayout>
  );
};

export default ResponsiveLayout;
