import React, { useState, useEffect } from "react";
import Chart from "react-apexcharts";

export const ChartGenerator = () => {
  const [chartData, setChartData] = useState([]);
  const [chartOptions, setChartOptions] = useState({});
  const [chartType, setChartType] = useState("bar");
  const [chartTitle, setChartTitle] = useState("Chart Title");
  const [chartLabels, setChartLabels] = useState([
    "Red",
    "Blue",
    "Yellow",
    "Green",
    "Purple",
    "Orange",
  ]);
  const [chartDataSets, setChartDataSets] = useState([
    {
      label: "# of Votes",
      data: [12, 19, 3, 5, 2, 3],
      backgroundColor: [
        "rgba(255, 99, 132, 0.2)",
        "rgba(54, 162, 235, 0.2)",
        "rgba(255, 206, 86, 0.2)",
        "rgba(75, 192, 192, 0.2)",
        "rgba(153, 102, 255, 0.2)",
        "rgba(255, 159, 64, 0.2)",
      ],
      borderColor: [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(255, 206, 86, 1)",
        "rgba(75, 192, 192, 1)",
        "rgba(153, 102, 255, 1)",
        "rgba(255, 159, 64, 1)",
      ],
      borderWidth: 1,
    },
  ]);

  useEffect(() => {
    setChartData({
      labels: chartLabels,
      datasets: chartDataSets,
    });
    setChartOptions({
      title: {
        display: true,
        text: chartTitle,
        fontSize: 20,
      },
      legend: {
        display: true,
        position: "right",
      },
    });
  }, [chartTitle, chartLabels, chartDataSets]);

  const handleChartTypeChange = (e) => {
    setChartType(e.target.value);
  };

  const handleChartTitleChange = (e) => {
    setChartTitle(e.target.value);
  };

  const handleChartLabelsChange = (e) => {
    setChartLabels(e.target.value.split(","));
  };

  const handleChartDatasets = (e) => {
    setChartDataSets(e.target.value.split(","));
  };

  return (
    <div className="chart-generator">
      <div className="chart-generator__controls">
        <div className="chart-generator__control">
          <label htmlFor="chart-type">Chart Type</label>
          <select
            name="chart-type"
            id="chart-type"
            onChange={handleChartTypeChange}
          >
            <option value="bar">Bar</option>
            <option value="line">Line</option>
            <option value="pie">Pie</option>
          </select>
        </div>
        <div className="chart-generator__control">
          <label htmlFor="chart-title">Chart Title</label>
          <input
            type="text"
            id="chart-title"
            onChange={handleChartTitleChange}
          />
        </div>
        <div className="chart-generator__control">
          <label htmlFor="chart-labels">Chart Labels</label>
          <input
            type="text"
            id="chart-labels"
            onChange={handleChartLabelsChange}
          />
        </div>
        <div className="chart-generator__control">
          <label htmlFor="chart-datasets">Chart Datasets</label>
          <input
            type="text"
            id="chart-datasets"
            onChange={handleChartDatasets}
          />
        </div>
      </div>
      <div className="chart-generator__chart">
        <Chart type={chartType} series={chartData} options={chartOptions} />
      </div>
    </div>
  );
};
