import { useState } from "react";
import { Box } from "@mui/system";
import { Responsive, WidthProvider } from "react-grid-layout";
const ResponsiveGridLayout = WidthProvider(Responsive);

// масив даних з координатами та розмірами
const layoutInitial = [
  { i: "a", x: 0, y: 0, w: 2, h: 4 },
  { i: "b", x: 2, y: 0, w: 3, h: 4 },
  { i: "c", x: 5, y: 0, w: 3, h: 4 },
  { i: "d", x: 8, y: 0, w: 2, h: 2 },
  { i: "e", x: 8, y: 2, w: 2, h: 2 },
];

const Healthcare = () => {
  const [layout, setLayout] = useState(
    JSON.parse(localStorage.getItem("layout")) || layoutInitial
  );

  const onLayoutChange = (newLayout) => {
    console.log(window.innerWidth);
    if (window.innerWidth < 900) {
      return;
    }

    setLayout(newLayout);
    localStorage.setItem("layout", JSON.stringify(newLayout));
  };

  return (
    <Box sx={{ width: "100%" }}>
      <h1 style={{ textAlign: "center" }}>Healthcare</h1>

      <ResponsiveGridLayout
        className="layout"
        layouts={{ lg: layout }}
        breakpoints={{ lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0 }}
        cols={{ lg: 12, md: 10, sm: 6, xs: 4, xxs: 2 }}
        rowHeight={30}
        // useCSSTransforms - оптимізація
        useCSSTransforms={true}
        // compactType - прилипання - горизонтальне або вертикальне
        compactType={"horizontal"}
        autoSize={true}
        // onLayoutChange={onLayoutChange}
      >
        <div key="a" style={{ backgroundColor: "tomato" }}>
          a
        </div>
        <div key="b" style={{ backgroundColor: "blue" }}>
          b
        </div>
        <div key="c" style={{ backgroundColor: "yellow" }}>
          c
        </div>
        <div key="d" style={{ backgroundColor: "green" }}>
          d
        </div>
        <div key="e" style={{ backgroundColor: "violet" }}>
          e
        </div>
        {/* <div key="f" style={{ backgroundColor: "aqua" }}>
          f
        </div> */}
      </ResponsiveGridLayout>
    </Box>
  );
};

export default Healthcare;
