import { Box, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import pages from "../../../pagesConfig";

export const CurrentPageTitle = () => {
  const [currentPage, setCurrentPage] = useState("");
  const { page, sub, group, home = "home" } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (currentPage) {
      let title = "";
      let prevPage = {};
      if (page) {
        prevPage = pages.find((elem) => elem.id === page);
        title = prevPage.title;
      }
      if (sub) {
        prevPage = prevPage.find((elem) => elem.id === sub);
        title = prevPage.title;
      }
      if (group) {
        prevPage = prevPage.find((elem) => elem.id === group);
        title = prevPage.title;
      }
      if (!sub && !page && !group) {
        const homePage = pages.find((elem) => elem.id === "home");
        if (!homePage) {
          navigate("/404");
          title = "404";
        }
        data = homePage.children[0];
      }

      currentPage(title);
    }
  }, [currentPage, group, navigate, page, sub]);

  return (
    <Box>
      <Typography>{currentPage}</Typography>
    </Box>
  );
};
