import React, { useState } from "react";
import List from "@mui/material/List";

import pages from "../../../pages/pagesConfig";
import { NavColapse } from "../NavColapse/NavColapse";
import { NavNoColapse } from "../NavNoColapse/NavNoColapse";

export const NavList = ({ open }) => {
  const [currentNav, setCurrentNav] = useState("");

  return (
    <List
      sx={{
        display: "flex",
        flexDirection: "column",
        height: "100%",
        justifyContent: "center",
        gap: "16px",
        "& svg": {
          fontSize: "2rem",
        },
      }}
    >
      {pages.map(({ id, title, icon, children }) =>
        children.length > 1 ? (
          <NavColapse
            key={id}
            id={id}
            title={title}
            icon={icon}
            sidebarOpen={open}
            children={children}
            currentNav={currentNav}
            setCurrentNav={setCurrentNav}
          />
        ) : (
          <NavNoColapse
            key={id}
            id={id}
            title={title}
            icon={icon}
            open={open}
            url={children[0].url}
            setCurrentNav={setCurrentNav}
          />
        )
      )}
    </List>
  );
};
