import React, { Suspense, useEffect, useState } from "react";
import { Box } from "@mui/material";
import { TestChart } from "./TestChart";

import { useParams } from "react-router-dom";
import ResponsiveLayout from "../../ResponsiveLayout/ResponsiveLayout";

const before = {
  content: "''",
  backgroundAttachment: "scroll",
  backgroundClip: "border-box",
  backgroundColor: "rgb(69, 39, 160)",
  backgroundImage: "none",
  backgroundOrigin: "padding-box",
  backgroundPositionX: "0%",
  backgroundPositionY: "0%",
  backgroundRepeatX: "repeat",
  backgroundRepeatY: "repeat",
  backgroundSize: "auto",
  borderBottomLeftRadius: "50%",
  borderBottomRightRadius: "50%",
  borderTopLeftRadius: "50%",
  borderTopRightRadius: "50%",
  boxSizing: "border-box",
  color: "rgb(255, 255, 255)",
  display: "block",
  fontFamily: "Roboto, sans-serif",
  fontSize: "14px",
  fontWeight: "400",
  height: "210px",
  lineHeight: "18.676px",
  opacity: "0.5",
  position: "absolute",
  right: "-15px",
  top: "-125px",
  width: "210px",
};

const after = {
  backgroundAttachment: "scroll",
  backgroundClip: "border-box",
  backgroundColor: "rgb(69, 39, 160)",
  backgroundImage: "none",
  backgroundOrigin: "padding-box",
  backgroundPositionX: "0%",
  backgroundPositionY: "0%",
  backgroundRepeatX: "repeat",
  backgroundRepeatY: "repeat",
  backgroundSize: "auto",
  borderBottomLeftRadius: "50%",
  borderBottomRightRadius: "50%",
  borderTopLeftRadius: "50%",
  borderTopRightRadius: "50%",
  boxSizing: "border-box",
  color: "rgb(255, 255, 255)",
  content: "''",
  display: "block",
  fontFamily: "Roboto, sans-serif",
  fontSize: "14px",
  fontWeight: "400",
  height: "210px",
  lineHeight: "18.676px",
  position: "absolute",
  right: "-95px",
  textSizeAdjust: "100%",
  top: "-85px",
  width: "210px",
  webkitFontSmoothing: "antialiased",
  zIndex: "1",
};

const PageLayoute = () => {
  const [currentPage, setCurrentPage] = useState();
  const { page, sub, group } = useParams();
  useEffect(() => {
    if (group) {
      setCurrentPage(group);
      return;
    }
    if (sub) {
      setCurrentPage(sub);
      return;
    }
    if (page) {
      setCurrentPage(page);
      return;
    }
  }, [page, sub, group]);

  return (
    <Box sx={{ width: "100%" }}>
      <h1>{currentPage}</h1>
      <ResponsiveLayout>
        <Box
          sx={{
            width: "400px",
            p: 2,
            display: "flex",
            flexDirection: "column",
            backgroundColor: "#5e35b1",
            borderRadius: "12px",
            overflow: "hidden",
            position: "relative",
            "&::before": before,
            "&::after": after,
          }}
        >
          <Box
            sx={{
              width: "100%",
              position: "relative",
              zIndex: "5",
            }}
          >
            <TestChart />
          </Box>
        </Box>
      </ResponsiveLayout>
    </Box>
  );
};

export default PageLayoute;

{
  layout.map((elem) => (
    <div
      key={elem.i}
      style={{
        backgroundColor: randomColor(),
        borderRadius: "6px",
        border: "1px solid red",
      }}
    >
      {elem.color}
    </div>
  ));
}
